import React, {useState, useEffect} from 'react';
import InfiniteScroll from 'react-infinite-scroll';
import Masonry from 'react-masonry-css';


import API from './API'; 
import Image from './Image';

let pageNum = 1;
function App() { 
  const [imageArray, setImageArray] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  const fetchImages = pageNumber => {
    API.get('/', {params: {page:pageNumber}})
    .then(res=>{
      setImageArray([...imageArray, res.data.hits])
      setTotalPages([res.data.totalHits / res.data.hits.length])
    })
    .catch(err=> {throw new Error('Ups, find an error while reading the data')})
  }

  useEffect(()=>{
    fetchImages(pageNum);
  }, [])

  return ( 
    <div className="App">
      <InfiniteScroll pageStart={0} loadMore={()=>{fetchImages()}} >
        <Masonry>
          <Image />
        </Masonry>
      </InfiniteScroll>
    </div>
  );
}

export default App;
